import kotlinx.coroutines.flow.stateIn
import kotlinx.coroutines.launch
import `in`.sitharaj.aurabudget.data.Budget
import `in`.sitharaj.aurabudget.repository.BudgetRepository
import javax.inject.Inject

@HiltViewModel
class BudgetViewModel @Inject constructor(
    private val budgetRepository: BudgetRepository
) : ViewModel() {

    val budgets = budgetRepository.getAllBudgets()
        .stateIn(
            scope = viewModelScope,
            started = SharingStarted.WhileSubscribed(5000),
            initialValue = emptyList()
        )

    fun addBudget(budget: Budget) {
        viewModelScope.launch {
            budgetRepository.insertBudget(budget)
        }
    }

    fun deleteBudget(budget: Budget) {
        viewModelScope.launch {
            budgetRepository.deleteBudget(budget)
        }
    }
}

package `in`.sitharaj.aurabudget.ui.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import dagger.hilt.android.lifecycle.HiltViewModel
import `in`.sitharaj.aurabudget.data.Category
import `in`.sitharaj.aurabudget.repository.CategoryRepository
import kotlinx.coroutines.flow.SharingStarted
import kotlinx.coroutines.flow.stateIn
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class CategoryViewModel @Inject constructor(
    private val categoryRepository: CategoryRepository
) : ViewModel() {

    val categories = categoryRepository.getAllCategories()
        .stateIn(
            scope = viewModelScope,
            started = SharingStarted.WhileSubscribed(5000),
            initialValue = emptyList()
        )

    fun addCategory(category: Category) {
        viewModelScope.launch {
            categoryRepository.insertCategory(category)
        }
    }

    fun deleteCategory(category: Category) {
        viewModelScope.launch {
            categoryRepository.deleteCategory(category)
        }
    }

    init {
        // Initialize with default categories if none exist
        viewModelScope.launch {
            addCategory(Category(name = "Food & Dining"))
            addCategory(Category(name = "Transportation"))
            addCategory(Category(name = "Shopping"))
            addCategory(Category(name = "Entertainment"))
            addCategory(Category(name = "Bills & Utilities"))
            addCategory(Category(name = "Healthcare"))
            addCategory(Category(name = "Travel"))
            addCategory(Category(name = "Other"))
        }
    }
}

