package `in`.sitharaj.aurabudget.data.local.entity
import androidx.room.Entity import androidx.room.PrimaryKey
/** * Room entity for Expense data * Data layer implementation separate from domain model */ @Entity(tableName = "expenses") data class ExpenseEntity( @PrimaryKey(autoGenerate = true) val id: Long = 0, val amount: Double, val categoryId: Long, val date: Long, val description: String?, val tags: String = "" // Store as comma-separated string )

package `in`.sitharaj.aurabudget.data.mapper

import `in`.sitharaj.aurabudget.data.local.entity.ExpenseEntity as ExpenseDataEntity
import `in`.sitharaj.aurabudget.domain.model.ExpenseEntity as ExpenseDomainEntity

/**
 * Mapper between data layer and domain layer entities
 * Following Single Responsibility Principle and Separation of Concerns
 */
object ExpenseMapper {

    fun mapToDomain(entity: ExpenseDataEntity): ExpenseDomainEntity {
        return ExpenseDomainEntity(
            id = entity.id,
            amount = entity.amount,
            categoryId = entity.categoryId,
            date = entity.date,
            description = entity.description,
            tags = entity.tags.split(",").filter { it.isNotBlank() }
        )
    }

    fun mapToData(domainEntity: ExpenseDomainEntity): ExpenseDataEntity {
        return ExpenseDataEntity(
            id = domainEntity.id,
            amount = domainEntity.amount,
            categoryId = domainEntity.categoryId,
            date = domainEntity.date,
            description = domainEntity.description,
            tags = domainEntity.tags.joinToString(",")
        )
    }

    fun mapToDomainList(entities: List<ExpenseDataEntity>): List<ExpenseDomainEntity> {
        return entities.map { mapToDomain(it) }
    }

    fun mapToDataList(domainEntities: List<ExpenseDomainEntity>): List<ExpenseDataEntity> {
        return domainEntities.map { mapToData(it) }
    }
}

package `in`.sitharaj.aurabudget.data.repository

import `in`.sitharaj.aurabudget.data.local.dao.BudgetDao
import `in`.sitharaj.aurabudget.data.mapper.BudgetMapper
import `in`.sitharaj.aurabudget.domain.model.BudgetEntity
import `in`.sitharaj.aurabudget.domain.repository.BudgetRepository
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.map
import javax.inject.Inject
import javax.inject.Singleton

/**
 * Implementation of BudgetRepository
 * Following Dependency Inversion Principle and Repository Pattern
 */
@Singleton
class BudgetRepositoryImpl @Inject constructor(
    private val budgetDao: BudgetDao
) : BudgetRepository {

    override fun getAllBudgets(): Flow<List<BudgetEntity>> {
        return budgetDao.getAllBudgets().map { entities ->
            BudgetMapper.mapToDomainList(entities)
        }
    }

    override fun getActiveBudgets(): Flow<List<BudgetEntity>> {
        return budgetDao.getActiveBudgets().map { entities ->
            BudgetMapper.mapToDomainList(entities)
        }
    }

    override fun getBudgetsByCategory(categoryId: Long): Flow<List<BudgetEntity>> {
        return budgetDao.getBudgetsByCategory(categoryId).map { entities ->
            BudgetMapper.mapToDomainList(entities)
        }
    }

    override suspend fun insertBudget(budget: BudgetEntity): Long {
        val dataEntity = BudgetMapper.mapToData(budget)
        return budgetDao.insertBudget(dataEntity)
    }

    override suspend fun updateBudget(budget: BudgetEntity) {
        val dataEntity = BudgetMapper.mapToData(budget)
        budgetDao.updateBudget(dataEntity)
    }

    override suspend fun deleteBudget(budget: BudgetEntity) {
        val dataEntity = BudgetMapper.mapToData(budget)
        budgetDao.deleteBudget(dataEntity)
    }

    override suspend fun getBudgetById(id: Long): BudgetEntity? {
        return budgetDao.getBudgetById(id)?.let { entity ->
            BudgetMapper.mapToDomain(entity)
        }
    }

    override suspend fun updateBudgetSpentAmount(budgetId: Long, spentAmount: Double) {
        budgetDao.updateBudgetSpentAmount(budgetId, spentAmount)
    }

    override fun getExpiredBudgets(): Flow<List<BudgetEntity>> {
        return budgetDao.getExpiredBudgets().map { entities ->
            BudgetMapper.mapToDomainList(entities)
        }
    }
}

package `in`.sitharaj.aurabudget.data.repository

import `in`.sitharaj.aurabudget.data.local.dao.ExpenseDao
import `in`.sitharaj.aurabudget.data.mapper.ExpenseMapper
import `in`.sitharaj.aurabudget.domain.model.ExpenseEntity
import `in`.sitharaj.aurabudget.domain.repository.ExpenseRepository
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.map
import java.util.*
import javax.inject.Inject
import javax.inject.Singleton

/**
 * Implementation of ExpenseRepository
 * Following Dependency Inversion Principle and Repository Pattern
 */
@Singleton
class ExpenseRepositoryImpl @Inject constructor(
    private val expenseDao: ExpenseDao
) : ExpenseRepository {

    override fun getAllExpenses(): Flow<List<ExpenseEntity>> {
        return expenseDao.getAllExpenses().map { entities ->
            ExpenseMapper.mapToDomainList(entities)
        }
    }

    override fun getExpensesByCategory(categoryId: Long): Flow<List<ExpenseEntity>> {
        return expenseDao.getExpensesByCategory(categoryId).map { entities ->
            ExpenseMapper.mapToDomainList(entities)
        }
    }

    override fun getExpensesByDateRange(startDate: Long, endDate: Long): Flow<List<ExpenseEntity>> {
        return expenseDao.getExpensesByDateRange(startDate, endDate).map { entities ->
            ExpenseMapper.mapToDomainList(entities)
        }
    }

    override suspend fun insertExpense(expense: ExpenseEntity): Long {
        val dataEntity = ExpenseMapper.mapToData(expense)
        return expenseDao.insertExpense(dataEntity)
    }

    override suspend fun updateExpense(expense: ExpenseEntity) {
        val dataEntity = ExpenseMapper.mapToData(expense)
        expenseDao.updateExpense(dataEntity)
    }

    override suspend fun deleteExpense(expense: ExpenseEntity) {
        val dataEntity = ExpenseMapper.mapToData(expense)
        expenseDao.deleteExpense(dataEntity)
    }

    override suspend fun getExpenseById(id: Long): ExpenseEntity? {
        return expenseDao.getExpenseById(id)?.let { entity ->
            ExpenseMapper.mapToDomain(entity)
        }
    }

    override fun getTotalExpensesForMonth(month: Int, year: Int): Flow<Double> {
        val calendar = Calendar.getInstance()
        calendar.set(year, month - 1, 1, 0, 0, 0)
        val startDate = calendar.timeInMillis

        calendar.add(Calendar.MONTH, 1)
        calendar.add(Calendar.MILLISECOND, -1)
        val endDate = calendar.timeInMillis

        return expenseDao.getTotalExpensesForPeriod(startDate, endDate)
    }

    override fun getExpensesGroupedByCategory(): Flow<Map<Long, List<ExpenseEntity>>> {
        return expenseDao.getAllExpenses().map { entities ->
            ExpenseMapper.mapToDomainList(entities).groupBy { it.categoryId }
        }
    }
}
}

