    return listOf(
        SettingsItemData(
            type = SettingsItemType.NAVIGATION,
            icon = Icons.Default.AttachMoney,
            title = "Currency",
            subtitle = "USD - United States Dollar",
            onClick = { navController.navigate("currency_settings") }
        ),
        SettingsItemData(
            type = SettingsItemType.NAVIGATION,
            icon = Icons.Default.Category,
            title = "Categories",
            subtitle = "Manage expense categories",
            onClick = { navController.navigate("categories") }
        ),
        SettingsItemData(
            type = SettingsItemType.NAVIGATION,
            icon = Icons.Default.AccountBalance,
            title = "Accounts",
            subtitle = "Manage bank accounts and cards",
            onClick = { navController.navigate("accounts") }
        )
    )
}

fun getDataPrivacyItems(viewModel: SettingsViewModel, navController: NavController): List<SettingsItemData> {
    return listOf(
        SettingsItemData(
            type = SettingsItemType.NAVIGATION,
            icon = Icons.Default.CloudUpload,
