import androidx.datastore.preferences.preferencesDataStore
import dagger.hilt.android.qualifiers.ApplicationContext
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.map
import javax.inject.Inject
import javax.inject.Singleton

private val Context.dataStore by preferencesDataStore(name = "user_prefs")

@Singleton
class UserPreferencesManager @Inject constructor(@ApplicationContext private val context: Context) {
    companion object {
        val DARK_THEME_KEY = booleanPreferencesKey("dark_theme")
    }

    val isDarkTheme: Flow<Boolean> = context.dataStore.data.map { prefs ->
        prefs[DARK_THEME_KEY] ?: false
    }

    suspend fun setDarkTheme(enabled: Boolean) {
        context.dataStore.edit { prefs ->
            prefs[DARK_THEME_KEY] = enabled
        }
    }
}

package `in`.sitharaj.aurabudget.data.local.dao

import androidx.room.*
import `in`.sitharaj.aurabudget.data.local.entity.BudgetEntity
import kotlinx.coroutines.flow.Flow

/**
 * DAO for Budget operations
 */
@Dao
interface BudgetDao {

    @Query("SELECT * FROM budgets ORDER BY startDate DESC")
    fun getAllBudgets(): Flow<List<BudgetEntity>>

    @Query("SELECT * FROM budgets WHERE endDate > :currentDate ORDER BY startDate DESC")
    fun getActiveBudgets(currentDate: Long = System.currentTimeMillis()): Flow<List<BudgetEntity>>

    @Query("SELECT * FROM budgets WHERE categoryId = :categoryId ORDER BY startDate DESC")
    fun getBudgetsByCategory(categoryId: Long): Flow<List<BudgetEntity>>

    @Query("SELECT * FROM budgets WHERE id = :id")
    suspend fun getBudgetById(id: Long): BudgetEntity?

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertBudget(budget: BudgetEntity): Long

    @Update
    suspend fun updateBudget(budget: BudgetEntity)

    @Delete
    suspend fun deleteBudget(budget: BudgetEntity)

    @Query("UPDATE budgets SET spent = :spentAmount WHERE id = :budgetId")
    suspend fun updateBudgetSpentAmount(budgetId: Long, spentAmount: Double)

    @Query("SELECT * FROM budgets WHERE endDate < :currentDate")
    fun getExpiredBudgets(currentDate: Long = System.currentTimeMillis()): Flow<List<BudgetEntity>>
}

package `in`.sitharaj.aurabudget.data.local.dao

import androidx.room.*
import `in`.sitharaj.aurabudget.data.local.entity.CategoryEntity
import kotlinx.coroutines.flow.Flow

/**
 * DAO for Category operations
 */
@Dao
interface CategoryDao {

    @Query("SELECT * FROM categories ORDER BY name ASC")
    fun getAllCategories(): Flow<List<CategoryEntity>>

    @Query("SELECT * FROM categories WHERE type = :type ORDER BY name ASC")
    fun getCategoriesByType(type: String): Flow<List<CategoryEntity>>

    @Query("SELECT * FROM categories WHERE id = :id")
    suspend fun getCategoryById(id: Long): CategoryEntity?

    @Query("SELECT * FROM categories WHERE isDefault = 1")
    fun getDefaultCategories(): Flow<List<CategoryEntity>>

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertCategory(category: CategoryEntity): Long

    @Update
    suspend fun updateCategory(category: CategoryEntity)

    @Delete
    suspend fun deleteCategory(category: CategoryEntity)

    @Query("SELECT COUNT(*) FROM categories")
    suspend fun getCategoryCount(): Int
}

package `in`.sitharaj.aurabudget.data.local.dao

import androidx.room.*
import `in`.sitharaj.aurabudget.data.local.entity.ExpenseEntity
import kotlinx.coroutines.flow.Flow

/**
 * Enhanced DAO for Expense operations with advanced queries
 * Following Interface Segregation Principle
 */
@Dao
interface ExpenseDao {

    @Query("SELECT * FROM expenses ORDER BY date DESC")
    fun getAllExpenses(): Flow<List<ExpenseEntity>>

    @Query("SELECT * FROM expenses WHERE categoryId = :categoryId ORDER BY date DESC")
    fun getExpensesByCategory(categoryId: Long): Flow<List<ExpenseEntity>>

    @Query("SELECT * FROM expenses WHERE date BETWEEN :startDate AND :endDate ORDER BY date DESC")
    fun getExpensesByDateRange(startDate: Long, endDate: Long): Flow<List<ExpenseEntity>>

    @Query("SELECT * FROM expenses WHERE id = :id")
    suspend fun getExpenseById(id: Long): ExpenseEntity?

    @Query("SELECT SUM(amount) FROM expenses WHERE date BETWEEN :startDate AND :endDate")
    fun getTotalExpensesForPeriod(startDate: Long, endDate: Long): Flow<Double>

    @Query("SELECT categoryId, SUM(amount) as total FROM expenses GROUP BY categoryId")
    fun getExpensesByCategoryTotal(): Flow<Map<Long, Double>>

    @Query("SELECT * FROM expenses WHERE description LIKE '%' || :searchQuery || '%' ORDER BY date DESC")
    fun searchExpenses(searchQuery: String): Flow<List<ExpenseEntity>>

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertExpense(expense: ExpenseEntity): Long

    @Update
    suspend fun updateExpense(expense: ExpenseEntity)

    @Delete
    suspend fun deleteExpense(expense: ExpenseEntity)

    @Query("DELETE FROM expenses WHERE id = :id")
    suspend fun deleteExpenseById(id: Long)

    @Query("SELECT COUNT(*) FROM expenses")
    suspend fun getExpenseCount(): Int
}

package `in`.sitharaj.aurabudget.data.local.dao

import androidx.room.*
import `in`.sitharaj.aurabudget.data.local.entity.ExpenseEntity
import kotlinx.coroutines.flow.Flow

/**
 * Data class for category totals
 */
data class CategoryTotal(
    val categoryId: Long,
    val total: Double
)

/**
 * Enhanced DAO for Expense operations with advanced queries
 * Following Interface Segregation Principle
 */
@Dao
interface ExpenseDao {

    @Query("SELECT * FROM expenses ORDER BY date DESC")
    fun getAllExpenses(): Flow<List<ExpenseEntity>>

    @Query("SELECT * FROM expenses WHERE categoryId = :categoryId ORDER BY date DESC")
    fun getExpensesByCategory(categoryId: Long): Flow<List<ExpenseEntity>>

    @Query("SELECT * FROM expenses WHERE date BETWEEN :startDate AND :endDate ORDER BY date DESC")
    fun getExpensesByDateRange(startDate: Long, endDate: Long): Flow<List<ExpenseEntity>>

    @Query("SELECT * FROM expenses WHERE id = :id")
    suspend fun getExpenseById(id: Long): ExpenseEntity?

    @Query("SELECT COALESCE(SUM(amount), 0.0) FROM expenses WHERE date BETWEEN :startDate AND :endDate")
    fun getTotalExpensesForPeriod(startDate: Long, endDate: Long): Flow<Double>

    @Query("SELECT categoryId, SUM(amount) as total FROM expenses GROUP BY categoryId")
    fun getExpensesByCategoryTotal(): Flow<List<CategoryTotal>>

    @Query("SELECT * FROM expenses WHERE description LIKE '%' || :searchQuery || '%' ORDER BY date DESC")
    fun searchExpenses(searchQuery: String): Flow<List<ExpenseEntity>>

    @Insert(onConflict = OnConflictStrategy.REPLACE)
    suspend fun insertExpense(expense: ExpenseEntity): Long

    @Update
    suspend fun updateExpense(expense: ExpenseEntity)

    @Delete
    suspend fun deleteExpense(expense: ExpenseEntity)

    @Query("DELETE FROM expenses WHERE id = :id")
    suspend fun deleteExpenseById(id: Long)

    @Query("SELECT COUNT(*) FROM expenses")
    suspend fun getExpenseCount(): Int
}

