package `in`.sitharaj.aurabudget.data.local.entity
import androidx.room.Entity import androidx.room.PrimaryKey
/** * Room entity for Expense data * Data layer implementation separate from domain model */ @Entity(tableName = "expenses") data class ExpenseEntity( @PrimaryKey(autoGenerate = true) val id: Long = 0, val amount: Double, val categoryId: Long, val date: Long, val description: String?, val tags: String = "" // Store as comma-separated string )

package `in`.sitharaj.aurabudget.data.mapper

import `in`.sitharaj.aurabudget.data.local.entity.BudgetEntity as BudgetDataEntity
import `in`.sitharaj.aurabudget.domain.model.BudgetEntity as BudgetDomainEntity

/**
 * Mapper between data layer and domain layer entities for Budget
 * Following Single Responsibility Principle and Separation of Concerns
 */
object BudgetMapper {

    fun mapToDomain(entity: BudgetDataEntity): BudgetDomainEntity {
        return BudgetDomainEntity(
            id = entity.id,
            name = entity.name,
            amount = entity.amount,
            spent = entity.spent,
            categoryId = entity.categoryId,
            startDate = entity.startDate,
            endDate = entity.endDate,
            isRecurring = entity.isRecurring
        )
    }

    fun mapToData(domainEntity: BudgetDomainEntity): BudgetDataEntity {
        return BudgetDataEntity(
            id = domainEntity.id,
            name = domainEntity.name,
            amount = domainEntity.amount,
            spent = domainEntity.spent,
            categoryId = domainEntity.categoryId,
            startDate = domainEntity.startDate,
            endDate = domainEntity.endDate,
            isRecurring = domainEntity.isRecurring
        )
    }

    fun mapToDomainList(entities: List<BudgetDataEntity>): List<BudgetDomainEntity> {
        return entities.map { mapToDomain(it) }
    }

    fun mapToDataList(domainEntities: List<BudgetDomainEntity>): List<BudgetDataEntity> {
        return domainEntities.map { mapToData(it) }
    }
}

package `in`.sitharaj.aurabudget.data.mapper

import `in`.sitharaj.aurabudget.data.local.entity.CategoryEntity as CategoryDataEntity
import `in`.sitharaj.aurabudget.domain.model.CategoryEntity as CategoryDomainEntity

/**
 * Mapper between data layer and domain layer entities for Category
 * Following Single Responsibility Principle and Separation of Concerns
 */
object CategoryMapper {

    fun mapToDomain(entity: CategoryDataEntity): CategoryDomainEntity {
        return CategoryDomainEntity(
            id = entity.id,
            name = entity.name,
            icon = entity.icon,
            color = entity.color,
            type = CategoryDomainEntity.CategoryType.valueOf(entity.type),
            isDefault = entity.isDefault,
            monthlyBudget = entity.monthlyBudget
        )
    }

    fun mapToData(domainEntity: CategoryDomainEntity): CategoryDataEntity {
        return CategoryDataEntity(
            id = domainEntity.id,
            name = domainEntity.name,
            icon = domainEntity.icon,
            color = domainEntity.color,
            type = domainEntity.type.name,
            isDefault = domainEntity.isDefault,
            monthlyBudget = domainEntity.monthlyBudget
        )
    }

    fun mapToDomainList(entities: List<CategoryDataEntity>): List<CategoryDomainEntity> {
        return entities.map { mapToDomain(it) }
    }

    fun mapToDataList(domainEntities: List<CategoryDomainEntity>): List<CategoryDataEntity> {
        return domainEntities.map { mapToData(it) }
    }
}

package `in`.sitharaj.aurabudget.data.mapper

import `in`.sitharaj.aurabudget.data.local.entity.ExpenseEntity as ExpenseDataEntity
import `in`.sitharaj.aurabudget.domain.model.ExpenseEntity as ExpenseDomainEntity

/**
 * Mapper between data layer and domain layer entities
 * Following Single Responsibility Principle and Separation of Concerns
 */
object ExpenseMapper {

    fun mapToDomain(entity: ExpenseDataEntity): ExpenseDomainEntity {
        return ExpenseDomainEntity(
            id = entity.id,
            amount = entity.amount,
            categoryId = entity.categoryId,
            date = entity.date,
            description = entity.description,
            tags = entity.tags.split(",").filter { it.isNotBlank() }
        )
    }

    fun mapToData(domainEntity: ExpenseDomainEntity): ExpenseDataEntity {
        return ExpenseDataEntity(
            id = domainEntity.id,
            amount = domainEntity.amount,
            categoryId = domainEntity.categoryId,
            date = domainEntity.date,
            description = domainEntity.description,
            tags = domainEntity.tags.joinToString(",")
        )
    }

    fun mapToDomainList(entities: List<ExpenseDataEntity>): List<ExpenseDomainEntity> {
        return entities.map { mapToDomain(it) }
    }

    fun mapToDataList(domainEntities: List<ExpenseDomainEntity>): List<ExpenseDataEntity> {
        return domainEntities.map { mapToData(it) }
    }
}

