                CategoryEntity(name = "Transportation", type = CategoryEntity.CategoryType.EXPENSE, isDefault = true),
                CategoryEntity(name = "Shopping", type = CategoryEntity.CategoryType.EXPENSE, isDefault = true),
                CategoryEntity(name = "Entertainment", type = CategoryEntity.CategoryType.EXPENSE, isDefault = true),
                CategoryEntity(name = "Bills & Utilities", type = CategoryEntity.CategoryType.EXPENSE, isDefault = true),
                CategoryEntity(name = "Healthcare", type = CategoryEntity.CategoryType.EXPENSE, isDefault = true),
                CategoryEntity(name = "Travel", type = CategoryEntity.CategoryType.EXPENSE, isDefault = true),
                CategoryEntity(name = "Other", type = CategoryEntity.CategoryType.EXPENSE, isDefault = true)
            )

            defaultCategories.forEach { category ->
                insertCategory(category)
            }
        }
    }
}

