import androidx.compose.ui.unit.dp
import androidx.navigation.NavController
import androidx.navigation.compose.currentBackStackEntryAsState

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun MainNavigationBar(
    navController: NavController,
    modifier: Modifier = Modifier
) {
    val navBackStackEntry by navController.currentBackStackEntryAsState()
    val currentRoute = navBackStackEntry?.destination?.route

    // Show bottom navigation only on main screens
    val showBottomNav = Screen.bottomNavItems.any { it.route == currentRoute }

    AnimatedVisibility(
        visible = showBottomNav,
        enter = slideInVertically(initialOffsetY = { it }),
        exit = slideOutVertically(targetOffsetY = { it }),
        modifier = modifier
    ) {
        NavigationBar(
            containerColor = MaterialTheme.colorScheme.surface,
            contentColor = MaterialTheme.colorScheme.onSurface,
            tonalElevation = 8.dp
        ) {
            Screen.bottomNavItems.forEach { screen ->
                NavigationBarItem(
                    icon = {
                        Icon(
                            imageVector = screen.icon ?: return@NavigationBarItem,
                            contentDescription = screen.title
                        )
                    },
                    label = { 
                        Text(
                            text = screen.title,
                            maxLines = 1,
                            overflow = TextOverflow.Ellipsis
                        ) 
                    },
                    selected = currentRoute == screen.route,
                    onClick = {
                        if (currentRoute != screen.route) {
                            navController.navigate(screen.route) {
                                // Pop up to the start destination to avoid building up a large stack
                                popUpTo(navController.graph.startDestinationId) {
                                    saveState = true
                                }
                                // Avoid multiple copies of the same destination
                                launchSingleTop = true
                                // Restore state when re-selecting a previously selected item
                                restoreState = true
                            }
                        }
                    },
                    colors = NavigationBarItemDefaults.colors(
                        selectedIconColor = MaterialTheme.colorScheme.primary,
                        selectedTextColor = MaterialTheme.colorScheme.primary,
                        indicatorColor = MaterialTheme.colorScheme.primaryContainer,
                        unselectedIconColor = MaterialTheme.colorScheme.onSurfaceVariant,
                        unselectedTextColor = MaterialTheme.colorScheme.onSurfaceVariant
                    )
                )
            }
        }
    }
}

package `in`.sitharaj.aurabudget.presentation.navigation
import androidx.compose.material.icons.Icons import androidx.compose.material.icons.filled.* import androidx.compose.ui.graphics.vector.ImageVector
sealed class Screen( val route: String, val title: String, val icon: ImageVector? = null ) { // Main Navigation Screens object Dashboard : Screen("dashboard", "Dashboard", Icons.Default.Home) object Transactions : Screen("transactions", "Transactions", Icons.Default.Receipt) object Budget : Screen("budget", "Budget", Icons.Default.AccountBalance) object Analytics : Screen("analytics", "Analytics", Icons.Default.Analytics) object Settings : Screen("settings", "Settings", Icons.Default.Settings)
// Secondary Screens object Splash : Screen("splash", "Splash") object Onboarding : Screen("onboarding", "Onboarding") object AddTransaction : Screen("add_transaction", "Add Transaction") object TransactionDetail : Screen("transaction_detail/{transactionId}", "Transaction Detail") { fun createRoute(transactionId: Long) = "transaction_detail/$transactionId" } object AddBudget : Screen("add_budget", "Add Budget") object BudgetDetail : Screen("budget_detail/{budgetId}", "Budget Detail") { fun createRoute(budgetId: Long) = "budget_detail/$budgetId" } object Categories : Screen("categories", "Categories") object AddCategory : Screen("add_category", "Add Category") object Goals : Screen("goals", "Goals") object AddGoal : Screen("add_goal", "Add Goal") object Profile : Screen("profile", "Profile") object Notifications : Screen("notifications", "Notifications") object Help : Screen("help", "Help") object About : Screen("about", "About")
companion object { val bottomNavItems = listOf(Dashboard, Transactions, Budget, Analytics, Settings) } }

package `in`.sitharaj.aurabudget.presentation.navigation
import androidx.compose.material.icons.Icons import androidx.compose.material.icons.filled.* import androidx.compose.ui.graphics.vector.ImageVector
sealed class Screen( val route: String, val title: String, val icon: ImageVector? = null ) { // Main Navigation Screens object Dashboard : Screen("dashboard", "Dashboard", Icons.Default.Home) object Transactions : Screen("transactions", "Transactions", Icons.Default.Receipt) object Budget : Screen("budget", "Budget", Icons.Default.AccountBalance) object Analytics : Screen("analytics", "Analytics", Icons.Default.Analytics) object Settings : Screen("settings", "Settings", Icons.Default.Settings)
// Secondary Screens object Splash : Screen("splash", "Splash") object Onboarding : Screen("onboarding", "Onboarding") object AddTransaction : Screen("add_transaction", "Add Transaction") object TransactionDetail : Screen("transaction_detail/{transactionId}", "Transaction Detail") { fun createRoute(transactionId: Long) = "transaction_detail/$transactionId" } object AddBudget : Screen("add_budget", "Add Budget") object BudgetDetail : Screen("budget_detail/{budgetId}", "Budget Detail") { fun createRoute(budgetId: Long) = "budget_detail/$budgetId" } object Categories : Screen("categories", "Categories") object AddCategory : Screen("add_category", "Add Category") object Goals : Screen("goals", "Goals") object AddGoal : Screen("add_goal", "Add Goal") object Profile : Screen("profile", "Profile") object Notifications : Screen("notifications", "Notifications") object Help : Screen("help", "Help") object About : Screen("about", "About")
companion object { val bottomNavItems = listOf(Dashboard, Transactions, Budget, Analytics) } }

package `in`.sitharaj.aurabudget.presentation.screen

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.Add
import androidx.compose.material.icons.filled.Menu
import androidx.compose.material.icons.filled.Settings
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import androidx.hilt.navigation.compose.hiltViewModel
import androidx.navigation.NavController
import `in`.sitharaj.aurabudget.presentation.viewmodel.ExpenseViewModel
import `in`.sitharaj.aurabudget.presentation.viewmodel.BudgetViewModel

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun EnhancedDashboardScreen(
    navController: NavController,
    expenseViewModel: ExpenseViewModel = hiltViewModel(),
    budgetViewModel: BudgetViewModel = hiltViewModel()
) {
    val expenses by expenseViewModel.expenses.collectAsState()
    val budgetUiState by budgetViewModel.uiState.collectAsState()

    Scaffold(
        topBar = {
            TopAppBar(
                title = { Text("Dashboard") },
                navigationIcon = {
                    IconButton(onClick = { /* TODO: Open drawer */ }) {
                        Icon(Icons.Default.Menu, contentDescription = "Menu")
                    }
                },
                actions = {
                    IconButton(onClick = { navController.navigate("settings") }) {
                        Icon(Icons.Default.Settings, contentDescription = "Settings")
                    }
                }
            )
        },
        floatingActionButton = {
            FloatingActionButton(
                onClick = { navController.navigate("expenses") }
            ) {
                Icon(Icons.Default.Add, contentDescription = "Add Expense")
            }
        }
    ) { paddingValues ->
        LazyColumn(
            modifier = Modifier
                .fillMaxSize()
                .padding(paddingValues)
                .padding(16.dp),
            verticalArrangement = Arrangement.spacedBy(16.dp)
        ) {
            item {
                // Total Balance Card
                Card(
                    modifier = Modifier.fillMaxWidth(),
                    colors = CardDefaults.cardColors(
                        containerColor = MaterialTheme.colorScheme.primaryContainer
                    )
                ) {
                    Column(
                        modifier = Modifier.padding(24.dp)
                    ) {
                        Text(
                            text = "Total Expenses",
                            fontSize = 16.sp,
                            color = MaterialTheme.colorScheme.onPrimaryContainer.copy(alpha = 0.7f)
                        )
                        Text(
                            text = "$${expenses.sumOf { it.amount }}",
                            fontSize = 32.sp,
                            fontWeight = FontWeight.Bold,
                            color = MaterialTheme.colorScheme.onPrimaryContainer
                        )
                    }
                }
            }

            item {
                Text(
                    text = "Recent Expenses",
                    fontSize = 20.sp,
                    fontWeight = FontWeight.SemiBold
                )
            }

            if (expenses.isEmpty()) {
                item {
                    Card(
                        modifier = Modifier.fillMaxWidth()
                    ) {
                        Column(
                            modifier = Modifier
                                .fillMaxWidth()
                                .padding(32.dp),
                            horizontalAlignment = Alignment.CenterHorizontally
                        ) {
                            Text(
                                text = "No expenses yet",
                                fontSize = 18.sp,
                                fontWeight = FontWeight.Medium
                            )
                            Text(
                                text = "Tap the + button to add your first expense",
                                fontSize = 14.sp,
                                color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.7f)
                            )
                        }
                    }
                }
            } else {
                items(expenses.take(5)) { expense ->
                    Card(
                        modifier = Modifier.fillMaxWidth()
                    ) {
                        Column(
                            modifier = Modifier.padding(16.dp)
                        ) {
                            Row(
                                modifier = Modifier.fillMaxWidth(),
                                horizontalArrangement = Arrangement.SpaceBetween,
                                verticalAlignment = Alignment.CenterVertically
                            ) {
                                Text(
                                    text = expense.description ?: "No description",
                                    fontSize = 16.sp,
                                    fontWeight = FontWeight.Medium
                                )
                                Text(
                                    text = "$${expense.amount}",
                                    fontSize = 16.sp,
                                    fontWeight = FontWeight.Bold,
                                    color = MaterialTheme.colorScheme.error
                                )
                            }
                        }
                    }
                }
            }
        }
    }
}

package `in`.sitharaj.aurabudget.presentation.screen

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.LazyRow
import androidx.compose.foundation.lazy.items
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.unit.dp
import androidx.navigation.NavController
import `in`.sitharaj.aurabudget.presentation.components.*
import `in`.sitharaj.aurabudget.presentation.navigation.Screen
import java.time.LocalDateTime

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun EnhancedDashboardScreen(
    navController: NavController,
    modifier: Modifier = Modifier
) {
    // Sample data - this should come from your ViewModels
    val balance = 45000.0
    val income = 75000.0
    val expenses = 30000.0

    val recentTransactions = listOf(
        Transaction(
            id = 1,
            amount = 2500.0,
            category = "Groceries",
            description = "Weekly shopping",
            type = TransactionType.EXPENSE,
            categoryIcon = Icons.Default.ShoppingCart
        ),
        Transaction(
            id = 2,
            amount = 50000.0,
            category = "Salary",
            description = "Monthly salary",
            type = TransactionType.INCOME,
            categoryIcon = Icons.Default.Work
        ),
        Transaction(
            id = 3,
            amount = 1200.0,
            category = "Transportation",
            description = "Fuel",
            type = TransactionType.EXPENSE,
            categoryIcon = Icons.Default.DirectionsCar
        ),
        Transaction(
            id = 4,
            amount = 800.0,
            category = "Entertainment",
            description = "Movie tickets",
            type = TransactionType.EXPENSE,
            categoryIcon = Icons.Default.Movie
        )
    )

    val categorySpending = listOf(
        Triple("Food & Dining", 8500.0, 12000.0) to Pair(Icons.Default.Restaurant, Color(0xFF4CAF50)),
        Triple("Transportation", 4200.0, 5000.0) to Pair(Icons.Default.DirectionsCar, Color(0xFF2196F3)),
        Triple("Shopping", 6800.0, 8000.0) to Pair(Icons.Default.ShoppingBag, Color(0xFFFF9800)),
        Triple("Entertainment", 2100.0, 3000.0) to Pair(Icons.Default.Movie, Color(0xFF9C27B0))
    )

    Column(
        modifier = modifier
            .fillMaxSize()
            .systemBarsPadding()
    ) {
        // Top App Bar with Enhanced Settings
        EnhancedTopAppBar(
            navController = navController,
            title = {
                Column {
                    Text("Good morning!")
                    Text(
                        "Welcome back",
                        style = MaterialTheme.typography.bodyMedium,
                        color = MaterialTheme.colorScheme.onSurfaceVariant
                    )
                }
            }
        )

        LazyColumn(
            modifier = Modifier.fillMaxSize(),
            contentPadding = PaddingValues(vertical = 16.dp),
            verticalArrangement = Arrangement.spacedBy(20.dp)
        ) {
            // Balance Card
            item {
                BalanceCard(
                    balance = balance,
                    income = income,
                    expenses = expenses,
                    modifier = Modifier.padding(horizontal = 16.dp),
                    onCardClick = { navController.navigate(Screen.Analytics.route) }
                )
            }

            // Quick Actions
            item {
                Column {
                    SectionHeader(
                        title = "Quick Actions",
                        modifier = Modifier.padding(horizontal = 16.dp)
                    )
                    Spacer(modifier = Modifier.height(12.dp))
                    QuickActionsRow(
                        onAddIncome = { navController.navigate(Screen.AddTransaction.route + "?type=income") },
                        onAddExpense = { navController.navigate(Screen.AddTransaction.route + "?type=expense") },
                        onTransfer = { navController.navigate(Screen.AddTransaction.route + "?type=transfer") },
                        onBudget = { navController.navigate(Screen.Budget.route) }
                    )
                }
            }

            // Category Spending Overview
            item {
                Column {
                    SectionHeader(
                        title = "Budget Overview",
                        subtitle = "This month's spending",
                        actionText = "View All",
                        onActionClick = { navController.navigate(Screen.Budget.route) },
                        modifier = Modifier.padding(horizontal = 16.dp)
                    )
                    Spacer(modifier = Modifier.height(12.dp))
                    LazyRow(
                        contentPadding = PaddingValues(horizontal = 16.dp),
                        horizontalArrangement = Arrangement.spacedBy(12.dp)
                    ) {
                        items(categorySpending) { (categoryData, iconColor) ->
                            val (name, spent, budget) = categoryData
                            val (icon, color) = iconColor
                            CategorySpendingCard(
                                categoryName = name,
                                amount = spent,
                                budget = budget,
                                icon = icon,
                                color = color,
                                modifier = Modifier.width(280.dp)
                            )
                        }
                    }
                }
            }

            // Recent Transactions
            item {
                SectionHeader(
                    title = "Recent Transactions",
                    subtitle = "Last ${recentTransactions.size} transactions",
                    actionText = "See All",
                    onActionClick = { navController.navigate(Screen.Transactions.route) },
                    modifier = Modifier.padding(horizontal = 16.dp)
                )
            }

            // Add individual transaction items directly to the main LazyColumn
            items(recentTransactions.take(4)) { transaction ->
                TransactionItem(
                    transaction = transaction,
                    onTransactionClick = {
                        navController.navigate(Screen.TransactionDetail.createRoute(transaction.id))
                    },
                    modifier = Modifier.padding(horizontal = 16.dp)
                )
            }

            // Financial Insights Card
            item {
                Card(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(horizontal = 16.dp),
                    colors = CardDefaults.cardColors(
                        containerColor = MaterialTheme.colorScheme.primaryContainer
                    )
                ) {
                    Column(
                        modifier = Modifier.padding(20.dp)
                    ) {
                        Row {
                            Icon(
                                Icons.Default.Lightbulb,
                                contentDescription = null,
                                tint = MaterialTheme.colorScheme.onPrimaryContainer
                            )
                            Spacer(modifier = Modifier.width(8.dp))
                            Text(
                                "Financial Insight",
                                style = MaterialTheme.typography.titleMedium,
                                color = MaterialTheme.colorScheme.onPrimaryContainer
                            )
                        }
                        Spacer(modifier = Modifier.height(8.dp))
                        Text(
                            "You're spending 15% less on dining out this month. Keep it up!",
                            style = MaterialTheme.typography.bodyMedium,
                            color = MaterialTheme.colorScheme.onPrimaryContainer
                        )
                        Spacer(modifier = Modifier.height(12.dp))
                        OutlinedButton(
                            onClick = { navController.navigate(Screen.Analytics.route) },
                            colors = ButtonDefaults.outlinedButtonColors(
                                contentColor = MaterialTheme.colorScheme.onPrimaryContainer
                            )
                        ) {
                            Text("View Analytics")
                        }
                    }
                }
            }

            // Bottom spacing for navigation bar
            item {
                Spacer(modifier = Modifier.height(80.dp))
            }
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun EnhancedTopAppBar(
    navController: NavController,
    title: @Composable () -> Unit,
    modifier: Modifier = Modifier
) {
    var showMoreMenu by remember { mutableStateOf(false) }
    var showSearchBar by remember { mutableStateOf(false) }
    var searchQuery by remember { mutableStateOf("") }

    if (showSearchBar) {
        // Search Top App Bar
        TopAppBar(
            title = {
                OutlinedTextField(
                    value = searchQuery,
                    onValueChange = { searchQuery = it },
                    placeholder = { Text("Search transactions...") },
                    singleLine = true,
                    modifier = Modifier.fillMaxWidth(),
                    colors = OutlinedTextFieldDefaults.colors(
                        focusedBorderColor = MaterialTheme.colorScheme.primary,
                        unfocusedBorderColor = MaterialTheme.colorScheme.outline
                    )
                )
            },
            navigationIcon = {
                IconButton(onClick = {
                    showSearchBar = false
                    searchQuery = ""
                }) {
                    Icon(Icons.Default.ArrowBack, contentDescription = "Close search")
                }
            },
            actions = {
                IconButton(onClick = {
                    // Handle search action
                    if (searchQuery.isNotEmpty()) {
                        navController.navigate("${Screen.Transactions.route}?search=$searchQuery")
                    }
                }) {
                    Icon(Icons.Default.Search, contentDescription = "Search")
                }
            },
            colors = TopAppBarDefaults.topAppBarColors(
                containerColor = MaterialTheme.colorScheme.surface
            ),
            modifier = modifier
        )
    } else {
        // Normal Top App Bar with Enhanced Actions
        TopAppBar(
            title = title,
            actions = {
                // Search Button
                IconButton(onClick = { showSearchBar = true }) {
                    Icon(Icons.Default.Search, contentDescription = "Search")
                }

                // Quick Filter Button
                IconButton(onClick = { navController.navigate("${Screen.Transactions.route}?filter=recent") }) {
                    Icon(Icons.Default.FilterList, contentDescription = "Filter")
                }

                // Notifications
                IconButton(onClick = { navController.navigate(Screen.Notifications.route) }) {
                    Icon(Icons.Default.Notifications, contentDescription = "Notifications")
                }

                // More Options Menu
                Box {
                    IconButton(onClick = { showMoreMenu = true }) {
                        Icon(Icons.Default.MoreVert, contentDescription = "More options")
                    }

                    DropdownMenu(
                        expanded = showMoreMenu,
                        onDismissRequest = { showMoreMenu = false }
                    ) {
                        // Theme Toggle
                        DropdownMenuItem(
                            text = { Text("Toggle Theme") },
                            onClick = {
                                showMoreMenu = false
                                // TODO: Toggle theme via ViewModel
                            },
                            leadingIcon = {
                                Icon(Icons.Default.Brightness4, contentDescription = null)
                            }
                        )

                        // Export Data
                        DropdownMenuItem(
                            text = { Text("Export Data") },
                            onClick = {
                                showMoreMenu = false
                                // TODO: Implement export functionality
                            },
                            leadingIcon = {
                                Icon(Icons.Default.CloudDownload, contentDescription = null)
                            }
                        )

                        // Backup & Sync
                        DropdownMenuItem(
                            text = { Text("Backup & Sync") },
                            onClick = {
                                showMoreMenu = false
                                // TODO: Navigate to backup settings
                            },
                            leadingIcon = {
                                Icon(Icons.Default.Backup, contentDescription = null)
                            }
                        )

                        Divider()

                        // Categories Management
                        DropdownMenuItem(
                            text = { Text("Manage Categories") },
                            onClick = {
                                showMoreMenu = false
                                navController.navigate(Screen.Categories.route)
                            },
                            leadingIcon = {
                                Icon(Icons.Default.Category, contentDescription = null)
                            }
                        )

                        // Goals (if available)
                        DropdownMenuItem(
                            text = { Text("Financial Goals") },
                            onClick = {
                                showMoreMenu = false
                                navController.navigate("goals") // Assuming you have goals screen
                            },
                            leadingIcon = {
                                Icon(Icons.Default.TrendingUp, contentDescription = null)
                            }
                        )

                        Divider()

                        // Profile
                        DropdownMenuItem(
                            text = { Text("Profile") },
                            onClick = {
                                showMoreMenu = false
                                navController.navigate(Screen.Profile.route)
                            },
                            leadingIcon = {
                                Icon(Icons.Default.AccountCircle, contentDescription = null)
                            }
                        )

                        // Settings
                        DropdownMenuItem(
                            text = { Text("Settings") },
                            onClick = {
                                showMoreMenu = false
                                navController.navigate(Screen.Settings.route)
                            },
                            leadingIcon = {
                                Icon(Icons.Default.Settings, contentDescription = null)
                            }
                        )

                        // Help & Support
                        DropdownMenuItem(
                            text = { Text("Help & Support") },
                            onClick = {
                                showMoreMenu = false
                                // TODO: Navigate to help screen
                            },
                            leadingIcon = {
                                Icon(Icons.Default.Help, contentDescription = null)
                            }
                        )
                    }
                }
            },
            colors = TopAppBarDefaults.topAppBarColors(
                containerColor = MaterialTheme.colorScheme.surface
            ),
            modifier = modifier
        )
    }
}

