package in.sitharaj.aurabudget

import android.app.Application
import dagger.hilt.android.HiltAndroidApp

@HiltAndroidApp
class AuraBudgetApp : Application()

package `in`.sitharaj.aurabudget

import android.os.Bundle
import androidx.activity.ComponentActivity
import androidx.activity.compose.setContent
import androidx.activity.enableEdgeToEdge
import androidx.compose.foundation.layout.fillMaxSize
import androidx.compose.foundation.layout.padding
import androidx.compose.material3.Scaffold
import androidx.compose.runtime.Composable
import androidx.compose.runtime.collectAsState
import androidx.compose.runtime.getValue
import androidx.compose.ui.Modifier
import androidx.hilt.navigation.compose.hiltViewModel
import androidx.navigation.compose.NavHost
import androidx.navigation.compose.composable
import androidx.navigation.compose.rememberNavController
import dagger.hilt.android.AndroidEntryPoint
import `in`.sitharaj.aurabudget.ui.screens.DashboardScreen
import `in`.sitharaj.aurabudget.ui.screens.ExpensesScreen
import `in`.sitharaj.aurabudget.ui.screens.SettingsScreen
import `in`.sitharaj.aurabudget.ui.screens.SplashScreen
import `in`.sitharaj.aurabudget.ui.theme.AuraBudgetTheme
import `in`.sitharaj.aurabudget.ui.viewmodel.ThemeViewModel

@AndroidEntryPoint
class MainActivity : ComponentActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        enableEdgeToEdge()
        setContent {
            AuraBudgetApp()
        }
    }
}

@Composable
fun AuraBudgetApp() {
    val themeViewModel: ThemeViewModel = hiltViewModel()
    val isDarkTheme by themeViewModel.isDarkTheme.collectAsState()

    AuraBudgetTheme(darkTheme = isDarkTheme) {
        val navController = rememberNavController()

        NavHost(
            navController = navController,
            startDestination = "splash"
        ) {
            composable("splash") {
                SplashScreen(onNavigateToMain = {
                    navController.navigate("dashboard") {
                        popUpTo("splash") { inclusive = true }
                    }
                })
            }
            composable("dashboard") {
                DashboardScreen(navController = navController)
            }
            composable("expenses") {
                ExpensesScreen(navController = navController)
            }
            composable("settings") {
                SettingsScreen(navController = navController)
            }
        }
    }
}

