        categoryRepositoryImpl: CategoryRepositoryImpl
    ): CategoryRepository

    @Binds
    @Singleton
    abstract fun bindPreferencesRepository(
        preferencesRepositoryImpl: PreferencesRepositoryImpl
    ): PreferencesRepository
}

package `in`.sitharaj.aurabudget.domain.model

/**
 * Domain model for Budget - Clean architecture entity
 * Represents budget planning and tracking
 */
data class BudgetEntity(
    val id: Long = 0,
    val name: String,
    val amount: Double,
    val spent: Double = 0.0,
    val categoryId: Long?,
    val startDate: Long,
    val endDate: Long,
    val isRecurring: Boolean = false
) {
    fun getRemainingAmount(): Double = amount - spent

    fun getSpentPercentage(): Float = if (amount > 0) (spent / amount).toFloat() else 0f

    fun isOverBudget(): Boolean = spent > amount

    fun isExpired(): Boolean = System.currentTimeMillis() > endDate

    fun getDaysRemaining(): Long {
        val currentTime = System.currentTimeMillis()
        return if (currentTime < endDate) {
            (endDate - currentTime) / (24 * 60 * 60 * 1000)
        } else 0
    }
}

package `in`.sitharaj.aurabudget.domain.model

/**
 * Domain model for Category - Clean architecture entity
 * Represents expense/income categories with enhanced features
 */
data class CategoryEntity(
    val id: Long = 0,
    val name: String,
    val icon: String?,
    val color: String?,
    val type: CategoryType = CategoryType.EXPENSE,
    val isDefault: Boolean = false,
    val monthlyBudget: Double? = null
) {
    enum class CategoryType {
        EXPENSE, INCOME, TRANSFER
    }

    fun hasValidName(): Boolean = name.isNotBlank()

    fun getDisplayName(): String = name.trim().takeIf { it.isNotEmpty() } ?: "Unnamed Category"
}

package `in`.sitharaj.aurabudget.domain.model

/**
 * Domain model for Expense - Clean architecture entity
 * Represents the core business entity without any framework dependencies
 */
data class ExpenseEntity(
    val id: Long = 0,
    val amount: Double,
    val categoryId: Long,
    val date: Long,
    val description: String?,
    val tags: List<String> = emptyList()
) {
    fun isValid(): Boolean {
        return amount > 0 && description?.isNotBlank() == true
    }

    fun getFormattedAmount(): String {
        return String.format("$%.2f", amount)
    }
}

package `in`.sitharaj.aurabudget.domain.model

/**
 * Domain model for Financial Insights and Analytics
 */
data class FinancialSummary(
    val totalIncome: Double,
    val totalExpenses: Double,
    val netAmount: Double,
    val categoryBreakdown: Map<CategoryEntity, Double>,
    val monthlyTrend: List<MonthlyData>,
    val topCategories: List<Pair<CategoryEntity, Double>>
) {
    fun getSavingsRate(): Double {
        return if (totalIncome > 0) ((totalIncome - totalExpenses) / totalIncome) * 100 else 0.0
    }

    fun isHealthyFinancially(): Boolean = netAmount > 0 && getSavingsRate() >= 20.0
}

data class MonthlyData(
    val month: String,
    val income: Double,
    val expenses: Double,
    val savings: Double
)

