import androidx.compose.material.icons.filled.Settings
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import androidx.hilt.navigation.compose.hiltViewModel
import androidx.navigation.NavController
import `in`.sitharaj.aurabudget.presentation.components.FinancialSummaryCard
import `in`.sitharaj.aurabudget.presentation.components.CategorySpendingItem
import `in`.sitharaj.aurabudget.presentation.components.ExpenseCard
import `in`.sitharaj.aurabudget.presentation.viewmodel.DashboardViewModel

/**
 * Enhanced Dashboard Screen with financial analytics and insights
 * Following MVVM pattern and clean architecture
 */
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun DashboardScreen(
    navController: NavController,
    viewModel: DashboardViewModel = hiltViewModel()
) {
    val uiState by viewModel.uiState.collectAsState()

    Scaffold(
        topBar = {
            TopAppBar(
                title = { 
                    Text(
                        "AuraBudget",
                        fontWeight = FontWeight.Bold,
                        color = MaterialTheme.colorScheme.primary
                    ) 
                },
                actions = {
                    IconButton(onClick = { navController.navigate("settings") }) {
                        Icon(Icons.Default.Settings, contentDescription = "Settings")
                    }
                },
                colors = TopAppBarDefaults.topAppBarColors(
                    containerColor = MaterialTheme.colorScheme.surface
                )
            )
        },
        floatingActionButton = {
            FloatingActionButton(
                onClick = { navController.navigate("expenses") },
                containerColor = MaterialTheme.colorScheme.primary
            ) {
                Icon(Icons.Default.Add, contentDescription = "Add Expense")
            }
        }
    ) { paddingValues ->

        if (uiState.isLoading) {
            Box(
                modifier = Modifier.fillMaxSize(),
                contentAlignment = Alignment.Center
            ) {
                CircularProgressIndicator()
            }
        } else {
            LazyColumn(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(paddingValues)
                    .padding(horizontal = 16.dp),
                verticalArrangement = Arrangement.spacedBy(20.dp)
            ) {
                item {
                    Spacer(modifier = Modifier.height(8.dp))
                }

                // Financial Summary Cards
                item {
                    LazyRow(
                        horizontalArrangement = Arrangement.spacedBy(12.dp),
                        contentPadding = PaddingValues(horizontal = 4.dp)
                    ) {
                        item {
                            FinancialSummaryCard(
                                title = "Total Expenses",
                                amount = uiState.totalExpenses,
                                subtitle = "This month",
                                trend = " 12%",
                                isPositive = false,
                                backgroundColor = MaterialTheme.colorScheme.errorContainer,
                                modifier = Modifier.width(200.dp)
                            )
                        }

                        item {
                            FinancialSummaryCard(
                                title = "Savings Rate",
                                amount = uiState.savingsRate,
                                subtitle = "Target: 20%",
                                trend = if (uiState.savingsRate >= 20) " On track" else " Below target",
                                isPositive = uiState.savingsRate >= 20,
                                backgroundColor = MaterialTheme.colorScheme.primaryContainer,
                                modifier = Modifier.width(200.dp)
                            )
                        }

                        item {
                            FinancialSummaryCard(
                                title = "Monthly Budget",
                                amount = uiState.monthlyBudget,
                                subtitle = "Remaining: $${uiState.monthlyBudget - uiState.totalExpenses}",
                                backgroundColor = MaterialTheme.colorScheme.secondaryContainer,
                                modifier = Modifier.width(200.dp)
                            )
                        }
                    }
                }

                // Top Categories Section
                if (uiState.topCategories.isNotEmpty()) {
                    item {
                        Card(
                            modifier = Modifier.fillMaxWidth(),
                            colors = CardDefaults.cardColors(
                                containerColor = MaterialTheme.colorScheme.surface
                            )
                        ) {
                            Column(
                                modifier = Modifier.padding(20.dp)
                            ) {
                                Text(
                                    text = "Top Spending Categories",
                                    fontSize = 18.sp,
                                    fontWeight = FontWeight.Bold,
                                    color = MaterialTheme.colorScheme.onSurface
                                )

                                Spacer(modifier = Modifier.height(16.dp))

                                uiState.topCategories.forEachIndexed { index, (categoryName, amount) ->
                                    val percentage = if (uiState.totalExpenses > 0) {
                                        (amount / uiState.totalExpenses * 100).toFloat()
                                    } else 0f

                                    CategorySpendingItem(
                                        categoryName = categoryName,
                                        amount = amount,
                                        percentage = percentage,
                                        color = getCategoryColor(index)
                                    )

                                    if (index < uiState.topCategories.size - 1) {
                                        Spacer(modifier = Modifier.height(12.dp))
                                    }
                                }
                            }
                        }
                    }
                }

                // Recent Expenses Section
                if (uiState.recentExpenses.isNotEmpty()) {
                    item {
                        Text(
                            text = "Recent Expenses",
                            fontSize = 20.sp,
                            fontWeight = FontWeight.Bold,
                            color = MaterialTheme.colorScheme.onBackground
                        )
                    }

                    items(uiState.recentExpenses) { expense ->
                        ExpenseCard(
                            expense = expense,
                            onEdit = { navController.navigate("expenses") },
                            onDelete = { /* TODO: Implement delete */ }
                        )
                    }
                }

                item {
                    Spacer(modifier = Modifier.height(100.dp))
                }
            }
        }
    }
}

@Composable
private fun getCategoryColor(index: Int): Color {
    val colors = listOf(
        Color(0xFF6200EE),
        Color(0xFF03DAC6),
        Color(0xFFFF6B6B),
        Color(0xFF4ECDC4),
        Color(0xFF45B7D1),
        Color(0xFF96CEB4),
        Color(0xFFFD79A8),
        Color(0xFFE84393)
    )
    return colors[index % colors.size]
}

package `in`.sitharaj.aurabudget.presentation.screen

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.foundation.text.KeyboardOptions
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.automirrored.filled.ArrowBack
import androidx.compose.material.icons.filled.Add
import androidx.compose.material.icons.filled.Search
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.input.KeyboardType
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import androidx.hilt.navigation.compose.hiltViewModel
import androidx.navigation.NavController
import `in`.sitharaj.aurabudget.presentation.components.ExpenseCard
import `in`.sitharaj.aurabudget.presentation.viewmodel.ExpenseViewModel

/**
 * Enhanced Expense Management Screen with search and filtering
 * Following MVVM pattern and clean architecture
 */
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun EnhancedExpenseScreen(
    navController: NavController,
    viewModel: ExpenseViewModel = hiltViewModel()
) {
    val expenses by viewModel.expenses.collectAsState()
    val uiState by viewModel.uiState.collectAsState()
    var showAddExpenseDialog by remember { mutableStateOf(false) }
    var searchQuery by remember { mutableStateOf("") }

    // Filter expenses based on search query
    val filteredExpenses = remember(expenses, searchQuery) {
        if (searchQuery.isEmpty()) {
            expenses
        } else {
            expenses.filter { expense ->
                expense.description?.contains(searchQuery, ignoreCase = true) == true ||
                expense.tags.any { it.contains(searchQuery, ignoreCase = true) }
            }
        }
    }

    LaunchedEffect(uiState.isExpenseAdded) {
        if (uiState.isExpenseAdded) {
            showAddExpenseDialog = false
            viewModel.resetExpenseAddedState()
        }
    }

    Scaffold(
        topBar = {
            TopAppBar(
                title = { 
                    Text(
                        "Expenses",
                        fontWeight = FontWeight.Bold
                    ) 
                },
                navigationIcon = {
                    IconButton(onClick = { navController.popBackStack() }) {
                        Icon(Icons.AutoMirrored.Filled.ArrowBack, contentDescription = "Back")
                    }
                },
                actions = {
                    IconButton(onClick = { /* TODO: Implement filter */ }) {
                        Icon(Icons.Default.Search, contentDescription = "Search")
                    }
                }
            )
        },
        floatingActionButton = {
            FloatingActionButton(
                onClick = { showAddExpenseDialog = true }
            ) {
                Icon(Icons.Default.Add, contentDescription = "Add Expense")
            }
        }
    ) { paddingValues ->
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(paddingValues)
                .padding(horizontal = 16.dp)
        ) {
            // Search Bar
            OutlinedTextField(
                value = searchQuery,
                onValueChange = { 
                    searchQuery = it
                    viewModel.searchExpenses(it)
                },
                label = { Text("Search expenses...") },
                leadingIcon = {
                    Icon(Icons.Default.Search, contentDescription = "Search")
                },
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(vertical = 16.dp)
            )

            // Expense Summary
            if (filteredExpenses.isNotEmpty()) {
                Card(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(bottom = 16.dp),
                    colors = CardDefaults.cardColors(
                        containerColor = MaterialTheme.colorScheme.primaryContainer
                    )
                ) {
                    Row(
                        modifier = Modifier
                            .fillMaxWidth()
                            .padding(16.dp),
                        horizontalArrangement = Arrangement.SpaceBetween
                    ) {
                        Column {
                            Text(
                                text = "Total Expenses",
                                fontSize = 14.sp,
                                color = MaterialTheme.colorScheme.onPrimaryContainer.copy(alpha = 0.7f)
                            )
                            Text(
                                text = String.format("$%.2f", filteredExpenses.sumOf { it.amount }),
                                fontSize = 24.sp,
                                fontWeight = FontWeight.Bold,
                                color = MaterialTheme.colorScheme.onPrimaryContainer
                            )
                        }
                        Column(
                            horizontalAlignment = Alignment.End
                        ) {
                            Text(
                                text = "Count",
                                fontSize = 14.sp,
                                color = MaterialTheme.colorScheme.onPrimaryContainer.copy(alpha = 0.7f)
                            )
                            Text(
                                text = "${filteredExpenses.size}",
                                fontSize = 24.sp,
                                fontWeight = FontWeight.Bold,
                                color = MaterialTheme.colorScheme.onPrimaryContainer
                            )
                        }
                    }
                }
            }

            // Expenses List
            if (uiState.isLoading) {
                Box(
                    modifier = Modifier.fillMaxSize(),
                    contentAlignment = Alignment.Center
                ) {
                    CircularProgressIndicator()
                }
            } else if (filteredExpenses.isEmpty()) {
                Box(
                    modifier = Modifier.fillMaxSize(),
                    contentAlignment = Alignment.Center
                ) {
                    Column(
                        horizontalAlignment = Alignment.CenterHorizontally
                    ) {
                        Text(
                            text = if (searchQuery.isEmpty()) "No expenses yet" else "No matching expenses",
                            fontSize = 18.sp,
                            fontWeight = FontWeight.Medium,
                            color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.6f)
                        )
                        Spacer(modifier = Modifier.height(8.dp))
                        Text(
                            text = if (searchQuery.isEmpty()) "Tap + to add your first expense" else "Try adjusting your search",
                            fontSize = 14.sp,
                            color = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.5f)
                        )
                    }
                }
            } else {
                LazyColumn(
                    verticalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    items(filteredExpenses) { expense ->
                        ExpenseCard(
                            expense = expense,
                            onEdit = { /* TODO: Implement edit */ },
                            onDelete = { /* TODO: Implement delete */ }
                        )
                    }

                    item {
                        Spacer(modifier = Modifier.height(80.dp))
                    }
                }
            }
        }
    }

    // Add Expense Dialog
    if (showAddExpenseDialog) {
        AddExpenseDialog(
            onDismiss = { showAddExpenseDialog = false },
            onAdd = { amount, description, tags ->
                viewModel.addExpense(
                    amount = amount,
                    categoryId = 1, // TODO: Category selection
                    description = description,
                    tags = tags
                )
            },
            isLoading = uiState.isLoading,
            error = uiState.error
        )
    }

    // Error handling
    uiState.error?.let { error ->
        LaunchedEffect(error) {
            // Show snackbar or handle error
            viewModel.clearError()
        }
    }
}

@Composable
private fun AddExpenseDialog(
    onDismiss: () -> Unit,
    onAdd: (Double, String, List<String>) -> Unit,
    isLoading: Boolean = false,
    error: String? = null
) {
    var amount by remember { mutableStateOf("") }
    var description by remember { mutableStateOf("") }
    var tags by remember { mutableStateOf("") }

    AlertDialog(
        onDismissRequest = onDismiss,
        title = { 
            Text(
                "Add New Expense",
                fontWeight = FontWeight.Bold
            ) 
        },
        text = {
            Column(
                verticalArrangement = Arrangement.spacedBy(12.dp)
            ) {
                OutlinedTextField(
                    value = amount,
                    onValueChange = { amount = it },
                    label = { Text("Amount") },
                    keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Decimal),
                    modifier = Modifier.fillMaxWidth(),
                    prefix = { Text("$") }
                )

                OutlinedTextField(
                    value = description,
                    onValueChange = { description = it },
                    label = { Text("Description") },
                    modifier = Modifier.fillMaxWidth(),
                    maxLines = 2
                )

                OutlinedTextField(
                    value = tags,
                    onValueChange = { tags = it },
                    label = { Text("Tags (comma separated)") },
                    modifier = Modifier.fillMaxWidth(),
                    placeholder = { Text("food, groceries, shopping") }
                )

                if (error != null) {
                    Text(
                        text = error,
                        color = MaterialTheme.colorScheme.error,
                        fontSize = 12.sp
                    )
                }
            }
        },
        confirmButton = {
            Button(
                onClick = {
                    val amountValue = amount.toDoubleOrNull()
                    if (amountValue != null && amountValue > 0 && description.isNotBlank()) {
                        val tagsList = tags.split(",").map { it.trim() }.filter { it.isNotEmpty() }
                        onAdd(amountValue, description, tagsList)
                    }
                },
                enabled = !isLoading && amount.toDoubleOrNull() != null && description.isNotBlank()
            ) {
                if (isLoading) {
                    CircularProgressIndicator(
                        modifier = Modifier.size(16.dp),
                        strokeWidth = 2.dp
                    )
                } else {
                    Text("Add")
                }
            }
        },
        dismissButton = {
            TextButton(
                onClick = onDismiss,
                enabled = !isLoading
            ) {
                Text("Cancel")
            }
        }
    )
}

package `in`.sitharaj.aurabudget.presentation.state
/** * UI State for Expense screen following MVVM pattern * Represents all possible UI states for expense management */ data class ExpenseUiState( val isLoading: Boolean = false, val error: String? = null, val isExpenseAdded: Boolean = false, val searchQuery: String = "", val selectedCategoryId: Long? = null, val isDialogVisible: Boolean = false, val expenseToEdit: `in`.sitharaj.aurabudget.domain.model.ExpenseEntity? = null )
/** * UI State for Dashboard with financial insights */ data class DashboardUiState( val isLoading: Boolean = false, val error: String? = null, val totalExpenses: Double = 0.0, val totalIncome: Double = 0.0, val monthlyBudget: Double = 0.0, val savingsRate: Double = 0.0, val topCategories: List<Pair<String, Double>> = emptyList(), val recentExpenses: List<`in`.sitharaj.aurabudget.domain.model.ExpenseEntity> = emptyList(), val monthlyTrend: List<`in`.sitharaj.aurabudget.domain.model.MonthlyData> = emptyList() )

package `in`.sitharaj.aurabudget.presentation.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import dagger.hilt.android.lifecycle.HiltViewModel
import `in`.sitharaj.aurabudget.domain.usecase.analytics.GetFinancialAnalyticsUseCase
import `in`.sitharaj.aurabudget.domain.usecase.expense.GetExpensesUseCase
import `in`.sitharaj.aurabudget.presentation.state.DashboardUiState
import kotlinx.coroutines.flow.*
import kotlinx.coroutines.launch
import javax.inject.Inject

/**
 * Enhanced DashboardViewModel with financial analytics and insights
 * Following MVVM pattern and SOLID principles
 */
@HiltViewModel
class DashboardViewModel @Inject constructor(
    private val getFinancialAnalyticsUseCase: GetFinancialAnalyticsUseCase,
    private val getExpensesUseCase: GetExpensesUseCase
) : ViewModel() {

    private val _uiState = MutableStateFlow(DashboardUiState())
    val uiState: StateFlow<DashboardUiState> = _uiState.asStateFlow()

    init {
        loadDashboardData()
    }

    private fun loadDashboardData() {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isLoading = true)

            combine(
                getFinancialAnalyticsUseCase.getFinancialSummary(),
                getExpensesUseCase().map { it.take(5) }
            ) { financialSummary, recentExpenses ->
                DashboardUiState(
                    isLoading = false,
                    totalExpenses = financialSummary.totalExpenses,
                    totalIncome = financialSummary.totalIncome,
                    savingsRate = financialSummary.getSavingsRate(),
                    topCategories = financialSummary.topCategories.map { (category, amount) ->
                        category.name to amount
                    },
                    recentExpenses = recentExpenses,
                    monthlyTrend = financialSummary.monthlyTrend
                )
            }.catch { error ->
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    error = error.message
                )
            }.collect { newState ->
                _uiState.value = newState
            }
        }
    }

    fun refreshData() {
        loadDashboardData()
    }

    fun clearError() {
        _uiState.value = _uiState.value.copy(error = null)
    }
}

package `in`.sitharaj.aurabudget.presentation.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import dagger.hilt.android.lifecycle.HiltViewModel
import `in`.sitharaj.aurabudget.domain.model.ExpenseEntity
import `in`.sitharaj.aurabudget.domain.usecase.expense.AddExpenseUseCase
import `in`.sitharaj.aurabudget.domain.usecase.expense.GetExpensesUseCase
import `in`.sitharaj.aurabudget.presentation.state.ExpenseUiState
import kotlinx.coroutines.flow.*
import kotlinx.coroutines.launch
import javax.inject.Inject

/**
 * Enhanced ExpenseViewModel following MVVM pattern and SOLID principles
 * Handles UI state management and business logic coordination
 */
@HiltViewModel
class ExpenseViewModel @Inject constructor(
    private val getExpensesUseCase: GetExpensesUseCase,
    private val addExpenseUseCase: AddExpenseUseCase
) : ViewModel() {

    private val _uiState = MutableStateFlow(ExpenseUiState())
    val uiState: StateFlow<ExpenseUiState> = _uiState.asStateFlow()

    val expenses = getExpensesUseCase()
        .stateIn(
            scope = viewModelScope,
            started = SharingStarted.WhileSubscribed(5000),
            initialValue = emptyList()
        )

    fun addExpense(
        amount: Double,
        categoryId: Long,
        description: String,
        tags: List<String> = emptyList()
    ) {
        viewModelScope.launch {
            _uiState.value = _uiState.value.copy(isLoading = true, error = null)

            val expense = ExpenseEntity(
                amount = amount,
                categoryId = categoryId,
                date = System.currentTimeMillis(),
                description = description,
                tags = tags
            )

            addExpenseUseCase(expense)
                .onSuccess {
                    _uiState.value = _uiState.value.copy(
                        isLoading = false,
                        isExpenseAdded = true
                    )
                }
                .onFailure { error ->
                    _uiState.value = _uiState.value.copy(
                        isLoading = false,
                        error = error.message
                    )
                }
        }
    }

    fun getExpensesByCategory(categoryId: Long): Flow<List<ExpenseEntity>> {
        return getExpensesUseCase.getByCategory(categoryId)
    }

    fun searchExpenses(query: String) {
        _uiState.value = _uiState.value.copy(searchQuery = query)
    }

    fun clearError() {
        _uiState.value = _uiState.value.copy(error = null)
    }

    fun resetExpenseAddedState() {
        _uiState.value = _uiState.value.copy(isExpenseAdded = false)
    }
}

